// src/features/supplier/commands/supplier.commands.ts
init_prisma();
var SupplierCommands = {
  async create(data) {
    const existingSupplier = await db.supplier.findUnique({
      where: {
        cnpj_storeId: {
          cnpj: data.cnpj,
          storeId: data.storeId || null
        }
      }
    });
    if (existingSupplier) {
      throw new Error("CNPJ already exists");
    }
    const responsiblesData = data.responsibles?.map((responsible) => ({
      name: responsible.name,
      phone: responsible.phone,
      email: responsible.email,
      cpf: responsible.cpf,
      status: true
    })) || [];
    return await db.supplier.create({
      data: {
        corporateName: data.corporateName,
        cnpj: data.cnpj,
        tradeName: data.tradeName,
        cep: data.cep,
        city: data.city,
        state: data.state,
        address: data.address,
        storeId: data.storeId,
        status: true,
        responsibles: {
          create: responsiblesData
        }
      },
      include: {
        responsibles: true,
        products: {
          select: {
            id: true,
            name: true,
            status: true
          }
        }
      }
    });
  },
  async update(id, data) {
    const existingSupplier = await db.supplier.findUnique({
      where: { id }
    });
    if (!existingSupplier) {
      throw new Error("Supplier not found");
    }
    if (data.cnpj && data.cnpj !== existingSupplier.cnpj) {
      const cnpjExists = await db.supplier.findUnique({
        where: {
          cnpj_storeId: {
            cnpj: data.cnpj,
            storeId: existingSupplier.storeId
          }
        }
      });
      if (cnpjExists) {
        throw new Error("CNPJ already exists");
      }
    }
    return await db.supplier.update({
      where: { id },
      data,
      include: {
        responsibles: true,
        products: {
          select: {
            id: true,
            name: true,
            status: true
          }
        }
      }
    });
  },
  async delete(id) {
    const existingSupplier = await db.supplier.findUnique({
      where: { id }
    });
    if (!existingSupplier) {
      throw new Error("Supplier not found");
    }
    const productsCount = await db.product.count({
      where: { supplierId: id }
    });
    if (productsCount > 0) {
      throw new Error("Cannot delete supplier with associated products");
    }
    return await db.supplier.delete({
      where: { id }
    });
  },
  async toggleStatus(id) {
    const supplier = await db.supplier.findUnique({
      where: { id }
    });
    if (!supplier) {
      throw new Error("Supplier not found");
    }
    return await db.supplier.update({
      where: { id },
      data: { status: !supplier.status },
      include: {
        responsibles: true,
        products: {
          select: {
            id: true,
            name: true,
            status: true
          }
        }
      }
    });
  }
};

// src/features/supplier/queries/supplier.queries.ts
init_prisma();
var SupplierQueries = {
  async getById(id) {
    const supplier = await db.supplier.findUnique({
      where: { id },
      include: {
        responsibles: true,
        products: {
          select: {
            id: true,
            name: true,
            status: true,
            referencePrice: true,
            stockMin: true,
            stockMax: true
          }
        }
      }
    });
    if (!supplier) {
      throw new Error("Supplier not found");
    }
    return supplier;
  },
  async list(params) {
    const { page = 1, limit = 10, search, status, storeId } = params;
    const skip2 = (page - 1) * limit;
    const where = {};
    if (search) {
      where.OR = [
        { corporateName: { contains: search, mode: "insensitive" } },
        { tradeName: { contains: search, mode: "insensitive" } },
        { cnpj: { contains: search } },
        { city: { contains: search, mode: "insensitive" } },
        { state: { contains: search, mode: "insensitive" } }
      ];
    }
    if (status !== void 0) {
      where.status = status;
    }
    if (storeId) {
      where.storeId = storeId;
    }
    const [suppliers, total] = await Promise.all([
      db.supplier.findMany({
        where,
        skip: skip2,
        take: limit,
        orderBy: { createdAt: "desc" },
        include: {
          responsibles: {
            where: { status: true }
          },
          products: {
            select: {
              id: true,
              name: true,
              status: true
            }
          }
        }
      }),
      db.supplier.count({ where })
    ]);
    return {
      items: suppliers,
      pagination: {
        page,
        limit,
        total,
        totalPages: Math.ceil(total / limit)
      }
    };
  },
  async getByCnpj(cnpj, storeId) {
    const supplier = await db.supplier.findUnique({
      where: { cnpj_storeId: { cnpj, storeId: storeId || null } },
      include: {
        responsibles: true,
        products: {
          select: {
            id: true,
            name: true,
            status: true
          }
        }
      }
    });
    if (!supplier) {
      throw new Error("Supplier not found");
    }
    return supplier;
  },
  async getByCity(city) {
    return await db.supplier.findMany({
      where: {
        city: { contains: city, mode: "insensitive" },
        status: true
      },
      orderBy: { corporateName: "asc" },
      include: {
        responsibles: {
          where: { status: true }
        }
      }
    });
  },
  async getByState(state) {
    return await db.supplier.findMany({
      where: {
        state: { contains: state, mode: "insensitive" },
        status: true
      },
      orderBy: { corporateName: "asc" },
      include: {
        responsibles: {
          where: { status: true }
        }
      }
    });
  },
  async getActive() {
    return await db.supplier.findMany({
      where: { status: true },
      orderBy: { corporateName: "asc" },
      select: {
        id: true,
        corporateName: true,
        tradeName: true,
        cnpj: true,
        city: true,
        state: true
      }
    });
  },
  async search(term, limit = 10) {
    return await db.supplier.findMany({
      where: {
        status: true,
        OR: [
          { corporateName: { contains: term, mode: "insensitive" } },
          { tradeName: { contains: term, mode: "insensitive" } },
          { cnpj: { contains: term } }
        ]
      },
      take: limit,
      orderBy: { corporateName: "asc" },
      select: {
        id: true,
        corporateName: true,
        tradeName: true,
        cnpj: true,
        city: true,
        state: true
      }
    });
  },
  async getStats() {
    const [total, active, inactive, withProducts] = await Promise.all([
      db.supplier.count(),
      db.supplier.count({ where: { status: true } }),
      db.supplier.count({ where: { status: false } }),
      db.supplier.count({
        where: {
          products: {
            some: {}
          }
        }
      })
    ]);
    return {
      total,
      active,
      inactive,
      withProducts,
      withoutProducts: total - withProducts
    };
  },
  async getTopSuppliers(limit = 5) {
    return await db.supplier.findMany({
      where: { status: true },
      orderBy: {
        products: {
          _count: "desc"
        }
      },
      take: limit,
      select: {
        id: true,
        corporateName: true,
        tradeName: true,
        cnpj: true,
        _count: {
          select: { products: true }
        }
      }
    });
  }
};

