// src/features/store/commands/store.commands.ts
init_prisma();
var StoreCommands = {
  async create(data) {
    const existingStore = await db.store.findUnique({
      where: { cnpj: data.cnpj }
    });
    if (existingStore) {
      throw new Error("CNPJ already exists");
    }
    const owner = await db.user.findUnique({
      where: { id: data.ownerId }
    });
    if (!owner) {
      throw new Error("Owner not found");
    }
    return await db.store.create({
      data: {
        ownerId: data.ownerId,
        name: data.name,
        cnpj: data.cnpj,
        email: data.email || null,
        phone: data.phone || null,
        cep: data.cep || null,
        city: data.city || null,
        state: data.state || null,
        address: data.address || null,
        status: data.status !== void 0 ? data.status : true
      },
      include: {
        owner: {
          select: {
            id: true,
            name: true,
            email: true
          }
        }
      }
    });
  },
  async update(id, data) {
    const existingStore = await db.store.findUnique({
      where: { id }
    });
    if (!existingStore) {
      throw new Error("Store not found");
    }
    if (data.cnpj && data.cnpj !== existingStore.cnpj) {
      const cnpjExists = await db.store.findUnique({
        where: { cnpj: data.cnpj }
      });
      if (cnpjExists) {
        throw new Error("CNPJ already exists");
      }
    }
    return await db.store.update({
      where: { id },
      data: {
        ...data,
        email: data.email === "" ? null : data.email,
        phone: data.phone === "" ? null : data.phone,
        cep: data.cep === "" ? null : data.cep,
        city: data.city === "" ? null : data.city,
        state: data.state === "" ? null : data.state,
        address: data.address === "" ? null : data.address
      },
      include: {
        owner: {
          select: {
            id: true,
            name: true,
            email: true
          }
        }
      }
    });
  },
  async delete(id) {
    const existingStore = await db.store.findUnique({
      where: { id }
    });
    if (!existingStore) {
      throw new Error("Store not found");
    }
    const productCount = await db.product.count({
      where: { storeId: id }
    });
    if (productCount > 0) {
      throw new Error("Cannot delete store with existing products");
    }
    return await db.store.delete({
      where: { id }
    });
  },
  async toggleStatus(id) {
    const store = await db.store.findUnique({
      where: { id }
    });
    if (!store) {
      throw new Error("Store not found");
    }
    return await db.store.update({
      where: { id },
      data: { status: !store.status },
      include: {
        owner: {
          select: {
            id: true,
            name: true,
            email: true
          }
        }
      }
    });
  },
  async verifyCnpj(cnpj) {
    const store = await db.store.findUnique({
      where: { cnpj }
    });
    return {
      exists: !!store,
      store: store ? {
        id: store.id,
        name: store.name,
        status: store.status
      } : null
    };
  },
  // === GERENCIAMENTO DE USURIOS DA LOJA ===
  async addUser(storeId, userId, role) {
    const store = await db.store.findUnique({
      where: { id: storeId }
    });
    if (!store) {
      throw new Error("Store not found");
    }
    const user = await db.user.findUnique({
      where: { id: userId, status: true }
    });
    if (!user) {
      throw new Error("User not found or inactive");
    }
    const existingStoreUser = await db.storeUser.findUnique({
      where: {
        storeId_userId: {
          storeId,
          userId
        }
      }
    });
    if (existingStoreUser) {
      throw new Error("User already exists in this store");
    }
    return await db.storeUser.create({
      data: {
        storeId,
        userId,
        role
      },
      include: {
        user: {
          select: {
            id: true,
            name: true,
            email: true,
            status: true,
            lastLoginAt: true
          }
        },
        store: {
          select: {
            id: true,
            name: true,
            cnpj: true
          }
        }
      }
    });
  },
  async removeUser(storeId, userId) {
    const store = await db.store.findUnique({
      where: { id: storeId }
    });
    if (!store) {
      throw new Error("Store not found");
    }
    const storeUser = await db.storeUser.findUnique({
      where: {
        storeId_userId: {
          storeId,
          userId
        }
      }
    });
    if (!storeUser) {
      throw new Error("User not found in this store");
    }
    if (storeUser.role === "OWNER") {
      throw new Error("Cannot remove store owner");
    }
    return await db.storeUser.delete({
      where: {
        storeId_userId: {
          storeId,
          userId
        }
      }
    });
  },
  async updateUserRole(storeId, userId, newRole) {
    const store = await db.store.findUnique({
      where: { id: storeId }
    });
    if (!store) {
      throw new Error("Store not found");
    }
    const storeUser = await db.storeUser.findUnique({
      where: {
        storeId_userId: {
          storeId,
          userId
        }
      }
    });
    if (!storeUser) {
      throw new Error("User not found in this store");
    }
    if (storeUser.role === "OWNER") {
      throw new Error("Cannot change store owner role");
    }
    return await db.storeUser.update({
      where: {
        storeId_userId: {
          storeId,
          userId
        }
      },
      data: {
        role: newRole
      },
      include: {
        user: {
          select: {
            id: true,
            name: true,
            email: true,
            status: true,
            lastLoginAt: true
          }
        },
        store: {
          select: {
            id: true,
            name: true,
            cnpj: true
          }
        }
      }
    });
  },
  async transferOwnership(storeId, newOwnerId) {
    const store = await db.store.findUnique({
      where: { id: storeId }
    });
    if (!store) {
      throw new Error("Store not found");
    }
    const newOwner = await db.user.findUnique({
      where: { id: newOwnerId, status: true }
    });
    if (!newOwner) {
      throw new Error("New owner not found or inactive");
    }
    const existingStoreUser = await db.storeUser.findUnique({
      where: {
        storeId_userId: {
          storeId,
          userId: newOwnerId
        }
      }
    });
    if (!existingStoreUser) {
      await db.storeUser.create({
        data: {
          storeId,
          userId: newOwnerId,
          role: "OWNER"
        }
      });
    } else {
      await db.storeUser.update({
        where: {
          storeId_userId: {
            storeId,
            userId: newOwnerId
          }
        },
        data: {
          role: "OWNER"
        }
      });
    }
    return await db.store.update({
      where: { id: storeId },
      data: {
        ownerId: newOwnerId
      },
      include: {
        owner: {
          select: {
            id: true,
            name: true,
            email: true
          }
        }
      }
    });
  }
};

